function prefix(n2k) {
  return `steering.thruster.bow`
}

module.exports = [
  {
    source: 'Identifier',
    node: function (n2k) {
      console.log(n2k);
      return `${prefix(n2k)}.identifier`
    }
  },
  {
    node: function (n2k) {
      return `${prefix(n2k)}.directionControl`
    },
    value: function (n2k, state) {
      return n2k.fields['Direction Control']
    }
  },
  {
    source: 'Power Enabled',
    node: function (n2k) {
      return `${prefix(n2k)}.powerEnabled`
    }
  },
  {
    source: 'Retract Control',
    node: function (n2k) {
      return `${prefix(n2k)}.retractControl`
    }
  },
  {
    node: function (n2k) {
      return `${prefix(n2k)}.speedControl`
    },
    value: function (n2k, state) {
      var speed = Number(n2k.fields['Speed Control'])
      return isNaN(speed) ? null : speed
    },
  },
  {
    source: 'Control Events',
    node: function (n2k) {
      return `${prefix(n2k)}.controlEvents`
    }
  },
  {
    node: function (n2k) {
      return `${prefix(n2k)}.commandTimeout`
    },
    value: function (n2k, state) {
      var timeout = Number(n2k.fields['Command Timeout'])
      return isNaN(timeout) ? null : timeout
    }
  },
  {
    node: function (n2k) {
      return `${prefix(n2k)}.azimuthControl`
    },
    value: function (n2k, state) {
      var azimuth = Number(n2k.fields['Azimuth Control'])
      return isNaN(azimuth) ? null : azimuth
    }
  }


]